mapper.select:
select * from member
where member_id = #{memberId} and
member_pw = #{memberPw}

mapper.update:
update member set
member_img = #{memberImg}
where member_num = #{memberNum}

mapper.select:
select member_img
from member
where member_num = #{memberNum}

select * from member where member_name = #{memberName}
and member_email =
#{memberEmail}

select * from member where member_id = #{memberId} and
member_email =
#{memberEmail}
